$_MODULE['<{statsmodule}prestashop>statsmodule_4bc24eed56e0ba89fc3ab4e094d18d8a'] = 'Sustainable operations: loyalty points or cards, which not only justify communication between merchant and client, but also offer advantages to clients (private offers, discounts).';
