$_MODULE['<{statsmodule}prestashop>statsmodule_4b69c1f7f555aa19fd90ee01e4aa63cd'] = 'In the tab, we break down the 10 most popular referral websites that bring customers to your online store.';
$_MODULE['<{statsmodule}prestashop>statsmodule_cec998cc46cd200fa97490137de2cc7f'] = 'What is a referral website?';
$_MODULE['<{statsmodule}prestashop>statsmodule_54f00c2c9a36e2b300b5bacc1bb7912c'] = 'The referrer is the URL of the previous webpage from which a link was followed by the visitor.';
$_MODULE['<{statsmodule}prestashop>statsmodule_7231fe46fc79eb2b3a269f790b79e01f'] = 'A referrer also enables you to know which keywords visitors use in search engines when browsing for your online store.';
$_MODULE['<{statsmodule}prestashop>statsmodule_af19c8da1c414055c960a73d86471119'] = 'A referrer can be:';
$_MODULE['<{statsmodule}prestashop>statsmodule_c227be237c874ba6b2f8771d7b66b90e'] = 'Someone who posts a link to your shop.';
$_MODULE['<{statsmodule}prestashop>statsmodule_ea87a2280d5cdb638a2727147a3dd85c'] = 'A partner who has agreed to a link exchange in order to attract new customers.';
