$_MODULE['<{statsmodule}prestashop>statsmodule_f083d0403991f2b0ae72ea8782518f36'] = 'Number of purchases compared to number of views';
$_MODULE['<{statsmodule}prestashop>statsmodule_6e13e61b8535a66feb27d285f5c42855'] = 'After choosing a category and selecting a product, informational graphs will appear.';
$_MODULE['<{statsmodule}prestashop>statsmodule_6734b1d16e064d64d9ea4affaed74cc7'] = 'If you notice that a product is often purchased but viewed infrequently, you should display it more prominently in your Front Office.';
$_MODULE['<{statsmodule}prestashop>statsmodule_f3a492ed300e8565dcdbbc1da340d226'] = 'On the other hand, if a product has many views but is not often purchased, we advise you to check or modify this product\'s information, description and photography again, see if you can find something better.';
